    private void pOrminusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pOrminusActionPerformed
       if(decimalClick==0){
        plusminus=(Double.parseDouble(String.valueOf(jDecimalField.getText())));
       plusminus=plusminus*(-1);
       jDecimalField.setText(String.valueOf(plusminus));
       }
    }//GEN-LAST:event_pOrminusActionPerformed

