    private void btnGetValueActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGetValueActionPerformed
       try{
           num2=jDecimalField.getValue();
     if(plusClick>0){
     total = num1 + num2;

     num1 = 0;
     num2 = 0;
     plusClick = 0;
     }


     if(minusClick>0){
     total = num1 - num2;

     num1 = 0;
     num2 = 0;
     minusClick = 0;

    }                                      
    
     if(multiplyClick>0){
     total = num1 * num2;
 
     num1 = 0;
     num2 = 0;
     multiplyClick = 0;
     
    }

     if(devideClick>0){
     total =num1 / num2;

     num1 = 0;
     num2 = 0;
     devideClick = 0;

    }
           
           
           
           
           jDecimalField.setText(  String.valueOf(total));
        }catch(NumberFormatException nfe){
            jDecimalField.setText("Non-Numeric");
        }
    }//GEN-LAST:event_btnGetValueActionPerformed

